import * as React from "react";
import App from "./App";
import {LinkContainer} from "react-router-bootstrap";
import Media from "react-bootstrap/Media";

export default class UserPage extends React.Component{
  state = {
    posts:[],
    loading:true
  };

  componentDidMount = async () => {
    await this.getUser();
  };

  componentWillReceiveProps = async () => {
    await this.getUser();
  };

  async getUser() {
    const { id } = await this.props.match.params;
    fetch(App.SITE_URL + "users/" + id +"/posts")
      .then(response => response.json())
      .then(data => this.setState({posts: data, loading:false}));
  }

  render() {
    if (this.state.loading) {
      return <h1>Loading, please wait</h1>;
    }
    return this.state.posts.map(post => {
      console.log(post);
      return <li key={post.postID}>{post.postTitle + " " + post.postID + "\n" + post.postContent}</li>;
    });
    }
}

export const ListItem = ({user}) => {
  return (
    <div key = {user.id}>
      <LinkContainer to={ "user/" + user.id }>
        <Media key = {user.postID}>
          <Media.Body>
            <br />
            <h5 style={{fontSize: "22px"}}>{ user.name }</h5>
          </Media.Body>
        </Media>
      </LinkContainer>
    </div>
  )
};
